import React from 'https://cdn.skypack.dev/react';

    import ReactDOM from 'https://cdn.skypack.dev/react-dom';
    import { Button, Container } from 'https://cdn.skypack.dev/@material-ui/core';
    const MyCustomComponent = ({data, updateData, trigerParent}) => {
   const [file, setFile] = React.useState();
	 const [isLoading, setIsLoading] = React.useState(false);
  const uploadRef = React.useRef();

  const progressRef = React.useRef();

  const UploadFile = () => {
    setIsLoading(true);
		const file = uploadRef.current.files[0];
    setFile(URL.createObjectURL(file));
    var formData = new FormData();
    formData.append("file", file);
   formData.append("lan", data.lan);
	 formData.append("key", data.key);
	 formData.append("type", "file");


formData.append("translate", data.translate);
    var xhr = new XMLHttpRequest();
    xhr.upload.addEventListener("progress", ProgressHandler, false);
    xhr.addEventListener("load", SuccessHandler, false);
    xhr.addEventListener("error", ErrorHandler, false);
    xhr.addEventListener("abort", AbortHandler, false);
    xhr.open(
      "POST",
     data.url+"/convertSpeech"
    );
    xhr.send(formData);
  };

  const ProgressHandler = (e) => {
    var percent = (e.loaded / e.total) * 100;
    progressRef.current.value = Math.round(percent);
  };

  const SuccessHandler = (e) => {
	 setIsLoading(false);
    progressRef.current.value = 0;
    uploadRef.current.value = '';
    if(e.currentTarget && e.currentTarget.response){
     trigerParent(e.currentTarget.response);
    }
  };
  const ErrorHandler = (e) => {
	 setIsLoading(false);
progressRef.current.value = 0;
uploadRef.current.value = '';
    if(e.currentTarget && e.currentTarget.response){trigerParent(e.currentTarget.response);
    }
  };
  const AbortHandler = (e) => {

    progressRef.current.value = 0;
    uploadRef.current.value = '';
    if(e.currentTarget && e.currentTarget.response){
     trigerParent(e.currentTarget.response);
    }
  };
    const mystyle = {
      display:"none"
    };

	const wrap={
	position:"relative",
		width: "100%",
    height: "100%",
    display: "flex",
    flexDirection: "revert",
    alignItems: "center",
    justifyContent: "space-around"

		}

		const labelInput = {
border: "1.5px solid #0a5dc2",
transition: "all 0.3s ease 0s",
padding: "2px 16px 4px 16px",
borderRadius: "10px",
width: "37%",
height: "25px",
textAlign: "center",
margin: "7px",
display: "flex",
alignItems: "center",
justifyContent: "center",
color: "#0a5dc2",
fontFamily:"Segoe UI",
cursor: isLoading  ? "not-allowed" : "pointer",
fontWeight: "400",
cursor:"pointer"
}


  return (
    <div style={wrap} className="App">
     <label style={labelInput}> Select file <input disabled={isLoading} style={mystyle} type="file" name="file" ref={uploadRef} onChange={UploadFile} />
		 </label>
      <label className="progressLabel">
         <progress ref={progressRef} value="0" max="100" />
      </label>
    </div>
  );
  }

    const ConnectedComponent = Tooljet.connectComponent(MyCustomComponent);
    ReactDOM.render(<ConnectedComponent />, document.body);
